import React from 'react';
import hospitalImg from '../assets/main.jpg'; // Ensure this is the correct path

export default function About() {
  return (
    <section id="about" className="bg-[#FFF9F0] py-20 px-4 md:px-10">
      <div className="max-w-7xl mx-auto grid md:grid-cols-2 gap-12 items-center">
        
        {/* Left - Hospital Image */}
        <div className="overflow-hidden rounded-xl shadow-lg animate__animated animate__fadeInLeft">
          <img
            src={hospitalImg}
            alt="Jakhar Ayurvedic Hospital"
            className="w-full h-auto object-cover rounded-xl"
          />
        </div>

        {/* Right - About Text */}
        <div className="space-y-6 animate__animated animate__fadeInRight">
          <h2 className="text-3xl md:text-4xl font-serif font-bold text-[#2F3E2E]">
            About Jakhar Ayurved Hospital
          </h2>
          <p className="text-[#4A4A4A] text-lg leading-relaxed">
            Located in the heart of Rohtak, Jakhar Hospital Ashtang Ayurved is a beacon of holistic healing and traditional wellness. We specialize in <span className="font-medium text-[#4A7C59]">authentic Panchkarma therapies, natural detoxification, chronic illness management, and mind-body balance</span>.
          </p>
          <p className="text-[#555]">
            Our Ayurvedic physicians and Panchkarma therapists bring decades of expertise, ensuring each treatment is personalized to your unique dosha and health journey. We invite you to experience a deeper healing — rooted in nature, nurtured by tradition.
          </p>
        </div>
      </div>
    </section>
  );
}
